// üìÅ BRAMHAND/frontend/src/pages/Landing.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Landing() {
  const navigate = useNavigate();

  const handleEnter = () => {
    const audio = new Audio('/bg-audio.mp3');
    audio.loop = true;
    audio.volume = 0.2;
    audio.play();
    navigate('/brahma');
  };

  return (
    <div className="relative h-screen w-full overflow-hidden bg-gradient-to-br from-[#000428] via-[#004e92] to-[#000428] flex flex-col items-center justify-center text-white">
      {/* Background stars and moving orbs */}
      <div className="absolute inset-0 bg-[url('https://www.transparenttextures.com/patterns/stardust.png')] opacity-10 mix-blend-overlay animate-pulse" />
      <div className="absolute w-full h-full overflow-hidden z-0">
        <div className="absolute w-72 h-72 bg-yellow-400 opacity-30 rounded-full top-1/3 left-1/4 blur-3xl animate-spin-slow" />
        <div className="absolute w-56 h-56 bg-indigo-500 opacity-20 rounded-full top-2/4 right-1/4 blur-2xl animate-ping" />
        <div className="absolute w-36 h-36 bg-pink-400 opacity-20 rounded-full top-1/4 left-[60%] blur-2xl animate-bounce" />
      </div>

      <div className="z-10 text-center">
        <h1 className="text-[80px] font-extrabold tracking-widest text-yellow-400 drop-shadow-[0_0_30px_rgba(255,223,0,0.8)] mb-4 animate-pulse">
          üî± BRAMHAND
        </h1>

        <div className="flex justify-center gap-12 text-5xl mb-8">
          <span className="animate-spin-slow">üå∏</span>
          <span className="text-blue-400 animate-ping">üåÄ</span>
          <span className="text-yellow-300 animate-bounce">üî±</span>
        </div>

        <p className="text-lg text-gray-300 italic max-w-2xl mx-auto mb-10 leading-relaxed">
          Step into the sacred realm of dataset purification ‚Äî where karma meets code.
        </p>

        <button
          onClick={handleEnter}
          className="bg-yellow-400 text-black text-2xl font-bold px-10 py-4 rounded-full shadow-2xl hover:scale-110 hover:shadow-[0_0_40px_rgba(255,255,0,0.7)] transition-all duration-500 ease-in-out"
        >
          ‚ú® Enter BRAMHAND ‚ú®
        </button>

        <p className="text-sm text-gray-400 mt-10">üìø Powered by the Wisdom of the Vedas üïâÔ∏è</p>
      </div>

      {/* Extra Animations */}
      <style>
        {`
          @keyframes spin-slow {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          .animate-spin-slow {
            animation: spin-slow 40s linear infinite;
          }
        `}
      </style>
    </div>
  );
}
