// üìÅ BRAMHAND/frontend/src/pages/Brahma.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

export default function Brahma() {
  const navigate = useNavigate();
  const [selectedFile, setSelectedFile] = useState(null);

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    setSelectedFile(file);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

  const sendFileToBackend = async () => {
    if (!selectedFile) {
      alert('Please upload a file first.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('‚úÖ Received Cosmic Metrics:', response.data);

      navigate('/vishnu', { state: { dataFromBackend: response.data } });

    } catch (error) {
      console.error('‚ùå Error sending file to backend:', error);
      alert('Failed to purify karma. Please try again.');
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-[#0f0c29] via-[#302b63] to-[#0f0c29] text-white p-8">
      <h1 className="text-5xl font-bold mb-8 animate-pulse">ü™î Brahma's Chamber of Creation</h1>

      <div
        {...getRootProps()}
        className="w-full max-w-3xl p-10 border-4 border-dashed border-yellow-400 rounded-xl bg-[#1a1a2e] flex flex-col items-center justify-center cursor-pointer hover:bg-[#16213e] transition"
      >
        <input {...getInputProps()} />
        {isDragActive ? (
          <p className="text-lg">Drop your sacred file here... üìÇ</p>
        ) : (
          <p className="text-lg">Drag & drop a file here, or click to select (CSV, Excel, JSON)</p>
        )}
      </div>

      {selectedFile && (
        <div className="mt-8 flex flex-col items-center">
          <p className="text-lg">Uploaded File: <span className="text-yellow-400">{selectedFile.name}</span></p>

          <button
            onClick={sendFileToBackend}
            className="mt-6 bg-yellow-400 hover:bg-yellow-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105"
          >
            ‚ú® Begin Karma Purification ‚ú®
          </button>
        </div>
      )}
    </div>
  );
}
