// üìÅ BRAMHAND/frontend/src/pages/Shiva.jsx
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { saveAs } from 'file-saver';

export default function Shiva() {
  const navigate = useNavigate();
  const location = useLocation();
  const receivedData = location.state?.data || [];

  const [finalData, setFinalData] = useState([]);

  const deepPurify = () => {
    if (!receivedData.length) return;

    // Replace missing/null/undefined/empty fields with 'NULL'
    const purifiedData = receivedData.map(row => {
      const newRow = {};
      Object.keys(row).forEach(key => {
        const value = row[key];
        newRow[key] = (value === null || value === undefined || value === '') ? 'NULL' : value;
      });
      return newRow;
    });

    setFinalData(purifiedData);
  };

  const downloadFinalFile = () => {
    if (!finalData.length) return;

    const csvContent = [
      Object.keys(finalData[0]).join(','),
      ...finalData.map(row => Object.values(row).join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    saveAs(blob, 'brahmhand_final_purified.csv');
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0f0c29] via-[#302b63] to-[#0f0c29] flex flex-col items-center justify-center p-6 text-white">
      <h1 className="text-5xl font-bold mb-6 animate-pulse">üî• Shiva's Chamber of Final Purification</h1>

      <button
        onClick={deepPurify}
        className="bg-red-400 hover:bg-red-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105 mb-8"
      >
        üßπ Replace Missing Values with 'NULL'
      </button>

      {finalData.length > 0 && (
        <div className="w-full max-w-5xl">
          <h2 className="text-2xl font-semibold mb-4">üßò‚Äç‚ôÇÔ∏è Fully Purified Karma Data:</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full table-auto text-sm text-gray-300">
              <thead>
                <tr>
                  {Object.keys(finalData[0]).map((col, index) => (
                    <th key={index} className="border px-4 py-2">{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {finalData.slice(0, 5).map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {Object.values(row).map((cell, colIndex) => (
                      <td key={colIndex} className="border px-4 py-2">
                        {cell !== undefined ? cell : '-'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="flex flex-col gap-4 mt-8">
            <button
              onClick={downloadFinalFile}
              className="bg-yellow-400 hover:bg-yellow-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105"
            >
              üì• Download Final Purified CSV
            </button>

            <button
              onClick={() => navigate('/summary', { state: { data: finalData } })}
              className="bg-green-400 hover:bg-green-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105"
            >
              üõï Proceed to Summary
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
