// üìÅ BRAMHAND/frontend/src/pages/Brahma.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

export default function Brahma() {
  const navigate = useNavigate();
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);  // NEW

  const onDrop = (acceptedFiles) => {
    setSelectedFile(acceptedFiles[0]);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      setUploading(true);  // NEW
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Backend Response:', response.data);  // üëà Important for debugging

      if (response.data && response.data.columns) {
        navigate('/vishnu', { state: { dataFromBackend: response.data } });
      } else {
        alert('Invalid response from backend.');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Failed to upload file. Check backend server.');
    } finally {
      setUploading(false);  // NEW
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0f0c29] via-[#302b63] to-[#0f0c29] flex flex-col items-center justify-center p-6 text-white">
      <h1 className="text-5xl font-bold mb-8 animate-pulse">ü™î Brahma's Chamber of Creation</h1>

      <div
        {...getRootProps()}
        className="w-full max-w-3xl p-10 border-4 border-dashed border-yellow-400 rounded-xl bg-[#1a1a2e] flex flex-col items-center justify-center cursor-pointer hover:bg-[#16213e] transition"
      >
        <input {...getInputProps()} />
        {isDragActive ? (
          <p className="text-lg">Drop your sacred file here... üìÇ</p>
        ) : (
          <p className="text-lg">Drag & drop a file here, or click to select (CSV, Excel, JSON)</p>
        )}
      </div>

      {selectedFile && (
        <div className="mt-6 text-lg">
          Uploaded File: <span className="text-yellow-400">{selectedFile.name}</span>
        </div>
      )}

      <button
        onClick={handleUpload}
        disabled={uploading}
        className="mt-8 bg-yellow-400 hover:bg-yellow-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105 disabled:opacity-50"
      >
        {uploading ? '‚è≥ Purifying...' : '‚ú® Begin Karma Purification ‚ú®'}
      </button>
    </div>
  );
}
