// 📁 BRAMHAND/frontend/src/pages/Vishnu.jsx
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function Vishnu() {
  const navigate = useNavigate();
  const location = useLocation();
  const uploadedData = location.state?.data || [];

  const [cleansedData, setCleansedData] = useState([]);
  const [isPurifying, setIsPurifying] = useState(false); // NEW: Track loading state

  const purifyData = () => {
    if (!uploadedData.length) return;

    setIsPurifying(true); // Start Loading

    setTimeout(() => {
      // Purification after a tiny delay, to allow UI to update
      const noEmptyRows = uploadedData.filter(row => {
        return Object.values(row).some(val => val !== '' && val !== null && val !== undefined);
      });

      const standardized = noEmptyRows.map(row => {
        const newRow = {};
        Object.keys(row).forEach(key => {
          const newKey = key.toLowerCase().replace(/\s+/g, '_');
          newRow[newKey] = row[key];
        });
        return newRow;
      });

      const uniqueRows = Array.from(new Set(standardized.map(JSON.stringify))).map(JSON.parse);

      setCleansedData(uniqueRows);
      setIsPurifying(false); // Done loading
    }, 100); // Slight timeout so UI shows loading text
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0f0c29] via-[#302b63] to-[#0f0c29] flex flex-col items-center justify-center p-6 text-white">
      <h1 className="text-5xl font-bold mb-6 animate-pulse">🔱 Vishnu's Chamber of Purification</h1>

      {!isPurifying && cleansedData.length === 0 && (
        <button
          onClick={purifyData}
          className="bg-green-400 hover:bg-green-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105 mb-8"
        >
          🧹 Start Purification
        </button>
      )}

      {isPurifying && (
        <div className="text-xl font-semibold mb-8 animate-pulse">
          🛕 Purifying your Karma... Please wait...
        </div>
      )}

      {cleansedData.length > 0 && (
        <div className="w-full max-w-5xl">
          <h2 className="text-2xl font-semibold mb-4">✨ Purified Karma Data (Soft Cleansing):</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full table-auto text-sm text-gray-300">
              <thead>
                <tr>
                  {Object.keys(cleansedData[0]).map((col, index) => (
                    <th key={index} className="border px-4 py-2">{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {cleansedData.slice(0, 5).map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {Object.values(row).map((cell, colIndex) => (
                      <td key={colIndex} className="border px-4 py-2">
                        {cell !== undefined ? cell : '-'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <button
            onClick={() => navigate('/shiva', { state: { data: cleansedData } })}
            className="mt-6 bg-yellow-400 hover:bg-yellow-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105"
          >
            🕉️ Proceed to Shiva
          </button>
        </div>
      )}
    </div>
  );
}
