// üìÅ BRAMHAND/frontend/src/pages/Shiva.jsx
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function Shiva() {
  const navigate = useNavigate();
  const location = useLocation();
  const dataFromBackend = location.state?.dataFromBackend;

  if (!dataFromBackend) {
    return (
      <div className="min-h-screen flex items-center justify-center text-white bg-gradient-to-b from-[#0f0c29] via-[#302b63] to-[#0f0c29]">
        <h1 className="text-3xl">üö´ No data found. Please start from Brahma page.</h1>
      </div>
    );
  }

  const { preview_data, columns } = dataFromBackend;

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0f0c29] via-[#302b63] to-[#0f0c29] flex flex-col items-center justify-start p-6 text-white">
      <h1 className="text-5xl font-bold mb-8 animate-pulse mt-8">üî• Shiva's Chamber of Final Purification üî•</h1>

      {/* Message */}
      <p className="text-lg text-center max-w-2xl mb-8">
        üßπ Your karma has undergone the cosmic fire of dissolution.  
        Only pure consciousness remains. You are ready for rebirth. üååüõï
      </p>

      {/* Optional - Mini Purified Data Preview */}
      <div className="w-full max-w-5xl mb-8">
        <h2 className="text-2xl font-semibold mb-4">üå∏ Final Glimpse of Purified Karma:</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full table-auto text-sm text-gray-300">
            <thead>
              <tr>
                {columns.map((col, index) => (
                  <th key={index} className="border px-4 py-2">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {preview_data.slice(0, 5).map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {columns.map((col, colIndex) => (
                    <td key={colIndex} className="border px-4 py-2">
                      {row[col] !== undefined ? row[col] : '-'}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Proceed to Summary Button */}
      <button
        onClick={() => navigate('/summary', { state: { dataFromBackend } })}
        className="mt-6 bg-yellow-400 hover:bg-yellow-500 text-black font-bold py-3 px-8 rounded-full transition transform hover:scale-105"
      >
        üõï Proceed to Rebirth and Karma Report
      </button>
    </div>
  );
}
